import { useEffect, useRef, useState } from 'react'
import axios from 'axios'
import ProductModal from '../../components/ProductModal'
import DeleteModal from '../../components/DeleteModal'
import { Modal } from 'bootstrap'
import Pagination from '../../components/Pagination'

function AdminProducts() {
  const [products, setProducts] = useState([])
  const [pagination, setPagination] = useState({})
  const [type, setType] = useState('create') //edit
  const [tempProduct, setTempProduct] = useState({})

  const productModal = useRef(null)
  const deleteModal = useRef(null)

  useEffect(() => {
    productModal.current = new Modal('#productModal', {
      backdrop: 'static'
    })
    deleteModal.current = new Modal('#deleteModal', {
      backdrop: 'static'
    })
    getProducts()
  }, [])

  const getProducts = async (page = 1) => {
    const productRes = await axios.get(
      `/v2/api/${process.env.REACT_APP_API_PATH}/admin/products?page=${page}`
    )
    setProducts(productRes.data.products)
    setPagination(productRes.data.pagination)
  }

  const openProductModal = (type, product) => {
    setType(type)
    setTempProduct(product)
    productModal.current.show()
  }

  const closeProductModal = () => {
    productModal.current.hide()
  }

  const openDeleteModal = (product) => {
    setTempProduct(product)
    deleteModal.current.show()
  }

  const closeDeleteModal = () => {
    deleteModal.current.hide()
  }

  const deleteProduct = async (id) => {
    try {
      const res = await axios.delete(
        `/v2/api/${process.env.REACT_APP_API_PATH}/admin/product/${id}`
      )
      if (res.data.success) {
        getProducts()
        closeDeleteModal()
      }
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div className='p-3'>
      <ProductModal
        closeProductModal={closeProductModal}
        getProducts={getProducts}
        tempProduct={tempProduct}
        type={type}
      ></ProductModal>
      <DeleteModal
        close={closeDeleteModal}
        text={tempProduct.title}
        handelDelete={deleteProduct}
        id={tempProduct.id}
      ></DeleteModal>
      <h3>產品列表</h3>
      <hr />
      <div className='text-end'>
        <button
          type='button'
          className='btn btn-primary btn-sm'
          onClick={() => {
            openProductModal('create', {})
          }}
        >
          建立新商品
        </button>
      </div>
      <table className='table'>
        <thead>
          <tr>
            <th scope='col'>分類</th>
            <th scope='col'>名稱</th>
            <th scope='col'>售價</th>
            <th scope='col'>啟用狀態</th>
            <th scope='col'>編輯</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => {
            return (
              <tr key={product.id}>
                <td>{product.category}</td>
                <td>{product.title}</td>
                <td>{product.price}</td>
                <td>{product.is_enabled ? '啟用' : '未啟用'}</td>
                <td>
                  <button
                    type='button'
                    className='btn btn-primary btn-sm'
                    onClick={() => {
                      openProductModal('edit', product)
                    }}
                  >
                    編輯
                  </button>
                  <button
                    type='button'
                    className='btn btn-outline-danger btn-sm ms-2'
                    onClick={() => {
                      openDeleteModal(product)
                    }}
                  >
                    刪除
                  </button>
                </td>
              </tr>
            )
          })}
        </tbody>
      </table>
      <Pagination pagination={pagination} changePage={getProducts}></Pagination>
    </div>
  )
}

export default AdminProducts
